import { PokerHandReplay, ErrorBoundary } from 'poker-hand-replay';

function CustomErrorFallback({ error, retry, canRetry, boundaryName }) {
  return (
    <div className="error-container">
      <h3>🚨 Hand Replay Error</h3>
      <p><strong>Error:</strong> {error.message}</p>
      <p><strong>Component:</strong> {boundaryName}</p>
      <details>
        <summary>Technical Details</summary>
        <pre>{error.stack}</pre>
      </details>
      {canRetry && (
        <button onClick={retry} className="retry-button">
          🔄 Try Again
        </button>
      )}
    </div>
  );
}

function App() {
  return (
    <ErrorBoundary 
      fallback={CustomErrorFallback}
      name="HandReplayContainer"
      maxRetries={3}
      enableLogging={true}
    >
      <PokerHandReplay 
        handHistory={handHistory}
        enableErrorRecovery={true}
      />
    </ErrorBoundary>
  );
}
